  `description` varchar(255) NOT NULL DEFAULT '',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;

/*Data for the table `aq_page` */

insert  into `aq_page`(`id`,`unique_id`,`parent_id`,`page_name`,`content`,`keywords`,`description`) values (1,'about',0,'公司简介','<div class=\"blockc\">\r\n	<strong>基本情况</strong><br />\r\n<b> </b>四川安全技术中心是根据安全生产需要，经中编办批准、省编办同意成立的省安全监管局（四川煤监局）直属事业单位；负责管理四川安全生\r\n产检测检验技术研究院、四川矿山安全技术培训中心和煤炭工业四川建设工程质量监督站。四川安全技术中心是一家集科研、政策研究、技术服务、安全培训工作为\r\n一体的生产性科技服务业机构。是全国15个“煤矿安全培训示范基地”之一；为国家重点建设的省级中心矿山安全实验室和省级中心职业危害检测与鉴定实验室的\r\n依托单位；为全国安全社区成都支持中心的依托单位；为四川省能源局认定的三家《煤矿企业瓦斯防治能力评估》评审单位之一；为四川省安全监管局、四川煤监\r\n局、四川省经信委认定的三家《煤矿安全质量标准化》考评机构之一；是全国六家煤炭采选业职业卫生技术服务甲级机构之一。<br />\r\n中心设有中国矿业大学成都函授站、工程硕士教学点。<br />\r\n拥有各类工程技术人员300余人，拥有国家安全生产技术专家2人、四川省安全生产行业专家43人。<br />\r\n中心在武侯科技园区建有5万平方米的四川安全科技大厦；在大邑县花水湾正在建设占地160余亩的四川安全生产应急实训科研出江基地。<br />\r\n<br />\r\n<strong>主要资质</strong><br />\r\n中心拥有职业卫生技术服务机构甲级资质、安全生产检测检验乙级资质、二级煤矿安全培训资质、二级安全培训资质、矿山建设工程质量监督资质、责任事故司法鉴\r\n定资质、煤矿瓦斯等级鉴定资质、安全防护设计资质、金属非金属矿山安全质量标准化考评资质、危险化学品安全生产标准化考评资质，冶金、有色、建材、机械、\r\n轻工、纺织、烟草、商贸等工贸行业安全生产标准化评审资质等资质。<br />\r\n<br />\r\n<strong>主要业务</strong><br />\r\n安全培训教育工作。主要承担全省监管监察系统公务员和工作人员业务培训、县（乡）领导干部安全生产业务知识培训；承担全省矿山专（兼）职救护队基层指挥员\r\n和队员培训；全省煤矿矿长资格培训；企业主要负责人和安全生产管理人员安全资格培训；企业专业技术人员业务知识及专业技能培训等。<br />\r\n矿用品检测检验工作。主要开展煤矿、非煤矿山在用安全设备检测检验等工作。<br />\r\n职业卫生技术服务工作。主要开展作业场所职业病危害因素检测，建设项目职业病危害预评价、控制效果评价、现状评价，劳动防护用品检测等工作。<br />\r\n责任事故司法鉴定工作。承担安全监察执法有关检测检验、验证分析工作；在资质范围内开展责任事故司法鉴定工作，为案件审理、监察执法、事故调查提供物证分析和技术鉴定报告。<br />\r\n矿山建设工程质量监督与认证工作。主要承担矿山建设项目的质量监督和工程质量认证等工作。<br />\r\n安全技术服务工作。为政府安全监管监察提供技术服务，为企业安全生产提供技术服务，为各级安全工作提供技术支持和智力保障。<br />\r\n安全科技研究和政策研究工作。主要开展安全应用技术研发与推广、安全生产政策研究、安全技术标准研究和安全技术物联网技术研究等工作。<br />\r\n安全社区建设服务工作。主要承担安全社区建设备案、现场指导，受政府委托进行现场评定等工作。<br />\r\n</div>','中心简介','中心简介'),(3,'history',1,'发展历程','发展历程','发展历程','发展历程'),(4,'contact',0,'联系我们','<span style=\"color:#D7D7D7;\">--------------------------------------------------------------------------------------------------------------------------------</span> \r\n<div>\r\n	<p style=\"text-align:left;\">\r\n		<span style=\"line-height:21.6px;\"><strong>地址：四川省成都市武侯区武兴四路16号安全科技大厦</strong></span> \r\n	</p>\r\n	<div style=\"text-align:left;\">\r\n		<span style=\"line-height:1.5;\">办公室：（028）87374426&nbsp;&nbsp;&nbsp;传真：（028）87374426&nbsp;</span>\r\n	</div>\r\n	<div style=\"text-align:left;\">\r\n		<span style=\"line-height:1.5;\">经营科：（028）&nbsp;87362617</span>\r\n	</div>\r\n	<div style=\"text-align:left;\">\r\n		<span style=\"line-height:1.5;\">财务科：（028)）61510141</span>\r\n	</div>\r\n	<div style=\"text-align:left;\">\r\n		<span style=\"line-height:1.5;\">技术管理科（总工办）：（028）61510245</span>\r\n	</div>\r\n	<div style=\"text-align:left;\">\r\n		<span style=\"line-height:1.5;\">安全培训中心：（028）61510453</span>\r\n	</div>\r\n	<div style=\"text-align:left;\">\r\n		<span style=\"line-height:1.5;\">矿用品检验研究所：（028）81716081</span>\r\n	</div>\r\n	<div style=\"text-align:left;\">\r\n		<span style=\"line-height:1.5;\">职业安全健康研究所(劳动防护技术研究所)：（028）61510042</span>\r\n	</div>\r\n	<div style=\"text-align:left;\">\r\n		<span style=\"line-height:1.5;\">煤炭工业四川建设工程质量监督站：（028）81716076</span>\r\n	</div>\r\n	<div style=\"text-align:left;\">\r\n		<span style=\"line-height:1.5;\">技术咨询服务所：（028）61510771</span>\r\n	</div>\r\n	<div style=\"text-align:left;\">\r\n		<span style=\"line-height:1.5;\">矿山仪器仪表检验所：（028）81716720</span>\r\n	</div>\r\n	<div style=\"text-align:left;\">\r\n		<span style=\"line-height:1.5;\">政策研究所（安全社区成都支持中心办公室）：（028）81711545</span>\r\n	</div>\r\n	<p style=\"text-align:left;\">\r\n		信息工程研究所：（028）61510242\r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		四川安全应急实训出江基地：（028）88308392\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<hr />\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<iframe src=\"http://kuangye.gogocd.com/admin/include/kindeditor/plugins/baidumap/index.html?center=103.984584%2C30.635849&amp;zoom=19&amp;width=558&amp;height=360&amp;markers=103.984584%2C30.635849&amp;markerStyles=l%2CA\" frameborder=\"0\" style=\"width:560px;height:362px;\">\r\n		</iframe>\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n</div>\r\n<br />','联系我们','联系我们'),(5,'job',0,'人才招聘','人才招聘','人才招聘','人才招聘');

/*Table structure for table `aq_product` */

DROP TABLE IF EXISTS `aq_product`;

CREATE TABLE `aq_product` (
  `id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `cat_id` smallint(5) NOT NULL DEFAULT '0',
  `name` varchar(150) NOT NULL DEFAULT '',
  `price` decimal(10,2) unsigned NOT NULL DEFAULT '0.00',
  `defined` text NOT NULL,
  `content` longtext NOT NULL,
  `image` varchar(255) NOT NULL DEFAULT '',
  `keywords` varchar(255) NOT NULL DEFAULT '',
  `add_time` int(10) unsigned NOT NULL DEFAULT '0',
  `description` varchar(255) NOT NULL DEFAULT '',
  `sort` tinyint(1) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

/*Data for the table `aq_product` */

/*Table structure for table `aq_product_category` */

DROP TABLE IF EXISTS `aq_product_category`;

CREATE TABLE `aq_product_category` (
  `cat_id` smallint(5) NOT NULL AUTO_INCREMENT,
  `unique_id` varchar(30) NOT NULL DEFAULT '',
  `cat_name` varchar(255) NOT NULL DEFAULT '',
  `keywords` varchar(255) NOT NULL DEFAULT '',
  `description` varchar(255) NOT NULL DEFAULT '',
  `parent_id` smallint(5) NOT NULL DEFAULT '0',
  `sort` tinyint(1) unsigned NOT NULL DEFAULT '50',
  PRIMARY KEY (`cat_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

/*Data for the table `aq_product_category` */

/*Table structure for table `aq_show` */

